import React, {useState} from 'react';
import {
  Button, StyleSheet, Text, View,
    ScrollView,
    TouchableOpacity,
    Animated,
    FlatList, 
  Dimensions,
  Alert
} from 'react-native';


import RegisteredStores from "./RegisteredStores";


export default function BusinessTypes(props) {
    const [items, setitems] = useState([
      {key:1, name:'General Store & Kiryana'},
      {key:2, name:'Medical Store & Baby Care'},
      {key:3, name:'Fruit & Vegitables'},
      {key:4, name:'Cloths1'},
      {key:4, name:'Cloths2'},
      {key:4, name:'Cloths3'},
      {key:4, name:'Cloths4'},
      {key:4, name:'Cloths5'},
     
    ]);

    const [storesList, setstoresList] = useState([]);

    const storesListRecords = [
        {
            image: '../../assets/icon.png',
            storeName: 'Bismillaha General Store',
            name: 'Mali Adnan',
            addressLine: 'Sabzi Manid Islambad, shop no 201',
            businessTypes:['General Store']
        },
        {
            image: '../../assets/icon.png',
            storeName: 'Bismillaha General Store',
            name: 'Mali Adnan',
            addressLine: 'Sabzi Manid Islambad, shop no 201',
            businessTypes:['General Store']
        },
        {
            image: '../../assets/icon.png',
            storeName: 'Bismillaha General Store',
            name: 'Mali Adnan',
            addressLine: 'Sabzi Manid Islambad, shop no 201',
            businessTypes:['General Store']
        },
        {
            image: '../../assets/icon.png',
            storeName: 'Bismillaha General Store',
            name: 'Mali Adnan',
            addressLine: 'Sabzi Manid Islambad, shop no 201',
            businessTypes:['General Store']
        },
        {
            image: '../../assets/icon.png',
            storeName: 'Bismillaha General Store',
            name: 'Mali Adnan',
            addressLine: 'Sabzi Manid Islambad, shop no 201',
            businessTypes:['General Store']
        },
    ]
    const handlePressEvent = (name) => {
        console.log(name)
        setstoresList(storesListRecords)
        
    }
     return (
      <>
         <ScrollView
             horizontal={true}
             style={styles.horizentalList}
         >

             {
                 items.map(it => {
                     return <TouchableOpacity
                         key={it.name}
                         onPress={()=>handlePressEvent(it.name)}
                     >
                         <Text
                             style={styles.item}
                         >{it.name}</Text>
                     </TouchableOpacity>
                 })
             }
         </ScrollView>

             {/* <RegisteredStoresStackScreen {...props}
                 data={storesList}
             >

             </RegisteredStoresStackScreen> */}
             <RegisteredStores {...props}
                 data={storesList}
             ></RegisteredStores>
             {
                //  storesList.map(store => {
                //      return <RegisteredStores
                //         data={}
                //      />
                //  })
             }
         
             </>
        
    );
}
  

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    horizentalList: {
      flex: 1,
    //    flexDirection:'column',
        // backgroundColor: 'blue',
        maxHeight:50,
      
      
    },
    item: {
      backgroundColor: '#4ae1fa',
       justifyContent: 'center',
      alignItems: 'center',
      textAlign:'center',
        margin: 5,
      
    }
  });
