import React, {useState, useEffect} from 'react';
import {
  Button, StyleSheet, Text, View,
    ScrollView,
    Image,
    TouchableOpacity,
    Animated,
    FlatList, 
  Dimensions,
  Alert,
    TextInput,
    Modal
} from 'react-native';



export default function Products({ route }) {
     const [modal, setmodal] = useState(false);
    console.log('route.params', route.params)
     const handleTouchEvent = () => { }
     
     const ShowModal =  ()=> {
        return (
            <Modal
            isVisible={modal}
            style={{ justifyContent: 'flex-end', margin: 0 }}
            onBackdropPress={() => setmodal(false)}>
    
            <View style={{ flex: 0.5, backgroundColor: 'white',justifyContent: 'space-around', alignItems: 'center' }}>
            </View>
    
        </Modal>
        )
     }
     
    return (
        <>
            <ScrollView>
                <TouchableOpacity
                    onPress={()=> setmodal(true)}

                >
                    <Image
                        source={require('../../assets/icon.png')}
                        style={{
                            width: 200,
                            height: 200,
                            
                        }}
                    />
                    <Text>Dall Mong</Text>
                    <Text>Net Weight: 25KG</Text>
                    <Text>Rs. 2500</Text>
                </TouchableOpacity>
                
                <TouchableOpacity
                    onPress={()=> setmodal(true)}

                >
                    <Image
                        source={require('../../assets/icon.png')}
                        style={{
                            width: 200,
                            height: 200,
                            
                        }}
                    />
                    <Text>Dall Chana</Text>
                    <Text>Net Weight: 25KG</Text>
                    <Text>Rs. 2500</Text>
               </TouchableOpacity>
                
            </ScrollView>

            <ShowModal />
           
        </>
    )
}
