import React, {useState, useEffect} from 'react';
import {
  Button, StyleSheet, Text, View,
    ScrollView,
    Image,
    TouchableOpacity,
    Animated,
    FlatList, 
  Dimensions,
  Alert,
    TextInput,
    Modal
} from 'react-native';



export default function Products({ route }) {
     const [modal, setmodal] = useState(false);
    console.log('route.params', route.params)
     const handleTouchEvent = () => { }
     
     const ShowModal =  ()=> {
        return (
            <Modal
  animationType="slide"
  transparent={true}
  visible={modal}
  onRequestClose={() => {
    // this.closeButtonFunction()
  }}>
  <View
    style={{
      height: '100%',
      marginTop: 'auto',
      backgroundColor:'blue'
    }}>
    <View style={styles.footer}>
      <Text style={styles.headerText}>This is Half Modal</Text>
    </View>
    <TouchableOpacity
      style={styles.addButton}
      onPress={() => {
        setmodal(!modal);
      }}>
      <Text style={styles.addButtonText}>Close</Text>
    </TouchableOpacity>
  </View>
</Modal>
        )
     }
     
    return (
        <>
            <ScrollView>
                <TouchableOpacity
                    onPress={()=> setmodal(true)}

                >
                    <Image
                        source={require('../../assets/icon.png')}
                        style={{
                            width: 200,
                            height: 200,
                            
                        }}
                    />
                    <Text>Dall Mong</Text>
                    <Text>Net Weight: 25KG</Text>
                    <Text>Rs. 2500</Text>
                </TouchableOpacity>
                
                <TouchableOpacity
                    onPress={()=> setmodal(true)}

                >
                    <Image
                        source={require('../../assets/icon.png')}
                        style={{
                            width: 200,
                            height: 200,
                            
                        }}
                    />
                    <Text>Dall Chana</Text>
                    <Text>Net Weight: 25KG</Text>
                    <Text>Rs. 2500</Text>
               </TouchableOpacity>
                
               <ShowModal />
            </ScrollView>

            
           
        </>
    )
}


const styles = StyleSheet.create({
    modalView: {
        margin: 0,
        justifyContent: 'flex-end'
    },
    containerStyle: {
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-around',
         alignItems: 'flex-end'
    },
    content: {
        width: '95%',
        height: '50%',
        backgroundColor: 'white',
        overflow: 'hidden',
      },
})
