import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import {
  Button, StyleSheet, Text, View,
  ScrollView
} from 'react-native';

import { NavigationContainer } from '@react-navigation/native';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';
import LoginStackScreen from "./src/screens/Login";
import HomeStackScreen from "./src/screens/Home";
import TabNavigator from 'react-native-tab-navigator';

const Drawer = createDrawerNavigator();
const Tab = createMaterialTopTabNavigator();

export default function App() {
  const [isLogin, setisLogin] = useState(true);

  function MyTabs() {
    return (
      <ScrollView horizontal={true} >
        <TabNavigator.Item
            // selected={this.state.selectedTab === 'home'}
            title="Home"
            // selectedTitleStyle={{color: "#3496f0"}}
            // tabStyle={styles.tabStyle}
            // titleStyle={styles.tabText}
        // onPress={() => this.setState({selectedTab: 'home'})}
        >
            <HomeStackScreen/>
          </TabNavigator.Item>
      <Tab.Navigator>
        <Tab.Screen name="Home" component={HomeStackScreen} />
        <Tab.Screen name="Profile" component={HomeStackScreen} />
        <Tab.Screen name="Settings" component={HomeStackScreen} />
        <Tab.Screen name="Contact" component={HomeStackScreen} />
        <Tab.Screen name="Details1" component={HomeStackScreen} />
        <Tab.Screen name="Details2" component={HomeStackScreen} />
        <Tab.Screen name="Details3" component={HomeStackScreen} />
        <Tab.Screen name="Details4" component={HomeStackScreen} />
        <Tab.Screen name="Details5" component={HomeStackScreen} />
        <Tab.Screen name="Details6" component={HomeStackScreen} />

        </Tab.Navigator>
        </ScrollView>
    );
  }
  return (
    <NavigationContainer>

      {isLogin === false &&
        <LoginStackScreen></LoginStackScreen>
      }

      {
        isLogin === true &&
        <Drawer.Navigator initialRouteName="Home">

          <Drawer.Screen name="Home" component={MyTabs} />
      </Drawer.Navigator>
      }


    </NavigationContainer>
  );
}
