import {
  Button, StyleSheet, Text, View,
    ScrollView,
    Animated,
    FlatList, 
  Dimensions
} from 'react-native';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';
const Tab = createMaterialTopTabNavigator();

export default function BusinessTypes() {
    const [items, setitems] = useState([
      {key:1, name:'General Store & Kiryana'},
      {key:2, name:'Medical Store & Baby Care'},
      {key:3, name:'Fruit & Vegitables'},
      {key:4, name:'Cloths1'},
      {key:4, name:'Cloths2'},
      {key:4, name:'Cloths3'},
      {key:4, name:'Cloths4'},
      {key:4, name:'Cloths5'},
     
    ]);
     return (
      
        //  <View>
        //      <Tab.Navigator>
        //      <Animated.FlatList
        //          horizontal={true}
        //          data={items}
        //          keyExtractor={item => item.key}
        //          renderItem={({item}) => {
        //              return <View>
        //                  <Text style={styles.item}>
        //                      {item.name}
        //                  </Text>
        //              </View>
                      
        //          }}
        //      >

        //          </Animated.FlatList>
        //          </Tab.Navigator>
        //  </View>
        
         <ScrollView  style={styles.horizentalList}>
             <Tab.Navigator>

             {
           items.map(it => {
                return    <Tab.Screen name={it.name} component={() => (<Text>{it.name }</Text>)} />
          })
        }
        
             </Tab.Navigator>
        
        
      </ScrollView>
    );
}
  

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    horizentalList: {
      flex: 1,
      // flexDirection:'column',
        backgroundColor: '#fff',
      
      
    },
    item: {
      backgroundColor: '#4ae1fa',
       justifyContent: 'center',
      alignItems: 'center',
      textAlign:'center',
        margin: 5,
      
    }
  });
