import React, {useState} from 'react';
import {
  Button, StyleSheet, Text, View,
  ScrollView
} from 'react-native';

import { createNativeStackNavigator } from '@react-navigation/native-stack';



const HomeScreen = () => {
    return (
      <View
        style={{flex:1, alignItems:'center', justifyContent:'center'}}
      >
        <Text>Home Screen</Text>
      </View>
    )
  }
  
  
  const HomeStack = createNativeStackNavigator();

const HomeStackScreen = () => {
    return (
    <HomeStack.Navigator screenOptions={{
          headerStyle: {
            backgroundColor:'#009387'
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight:'bold'
          }
        }}>
  
          <HomeStack.Screen name="Home" component={businessTypes}></HomeStack.Screen>
  
        </HomeStack.Navigator>
    )
  }
  

export default HomeStackScreen;

  function businessTypes() {
    const [items, setitems] = useState([
      {key:1, name:'General Store & Kiryana'},
      {key:2, name:'Medical Store & Baby Care'},
      {key:3, name:'Fruit & Vegitables'},
      {key:4, name:'Cloths'},
     
    ]);
    return (
      <ScrollView horizontal={true} style={styles.horizentalList}>
        {
          items.map(it => {
            return <View key={it.key}>
              <Text style={styles.item} >{ it.name }</Text>
              </View>
          })
        }
        
        
      </ScrollView>
    );
}
  

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    horizentalList: {
      flex: 1,
      // flexDirection:'column',
        backgroundColor: '#red',
      
      
    },
    item: {
      backgroundColor: '#4ae1fa',
       justifyContent: 'center',
      alignItems: 'center',
      textAlign:'center',
      margin:5,
      
    }
  });
