import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import {
  Button, StyleSheet, Text, View,
  ScrollView
} from 'react-native';

import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';



const LoginScreen = ({navigation}) => {
  return (
    <View
      style={{flex:1, alignItems:'center', justifyContent:'center'}}
    >
      <Text>Login Screen</Text>
      <Button
        title="Goto Detail screen"
      onPress={()=>{navigation.navigate('Details')}}
      ></Button>
    </View>
  )
}

const DetailScreen = () => {
  return (
    <View
      style={{flex:1, alignItems:'center', justifyContent:'center'}}
    >
      <Text>Detail Screen</Text>
    </View>
  )
}

const LoginStack = createNativeStackNavigator();
const HomeStack = createNativeStackNavigator();
const Drawer = createDrawerNavigator();
const Tab = createMaterialTopTabNavigator();

const LoginStackScreen = () => {
  return (
<LoginStack.Navigator >

        <LoginStack.Screen name="Login" component={LoginScreen}></LoginStack.Screen>

      </LoginStack.Navigator>
  )
}

const HomeStackScreen = () => {
  return (
<HomeStack.Navigator screenOptions={{
        headerStyle: {
          backgroundColor:'#009387'
        },
        headerTintColor: '#fff',
        headerTitleStyle: {
          fontWeight:'bold'
        }
      }}>

        <HomeStack.Screen name="Details" component={DetailScreen}></HomeStack.Screen>

      </HomeStack.Navigator>
  )
}


function MyTabs() {
  const [items, setitems] = useState([
    {key:1, name:'Item 1'},
    {key:2, name:'Item 4'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
    {key:3, name:'Item 3'},
  ]);
  return (
    <ScrollView style={styles.horizentalList}>
      {
        items.map(it => {
          return <View >
            <Text style={styles.item} key={it.key}>{ it.name }</Text>
            </View>
        })
      }


    </ScrollView>
  );
}
export default function App() {
  const [isLogin, setisLogin] = useState(true);

  return (
    <NavigationContainer>

      {isLogin === false &&
        <LoginStackScreen></LoginStackScreen>
      }

      {
        isLogin === true &&
        <Drawer.Navigator initialRouteName="Home">

          <Drawer.Screen name="Details" component={MyTabs} />
      </Drawer.Navigator>
      }


    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  horizentalList: {
    flex: 1,
    // flexDirection:'column',
    backgroundColor: '#fff',

  },
  item: {
    backgroundColor: '#4ae1fa',
     justifyContent: 'center',
    alignItems: 'center',
    textAlign:'center',
    margin:5,

  }
});
