import React, {useState, useEffect} from 'react';
import {
  Button, StyleSheet, Text, View,
    ScrollView,
    Image,
    TouchableOpacity,
    Animated,
    FlatList, 
  Dimensions,
  Alert
} from 'react-native';

import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';
const Tab = createMaterialTopTabNavigator();

export default function StoreDetailsTabs(props) {
    return (
        <Tab.Navigator>
            <Tab.Screen name="Detail"   >
            { ()=> <StoreDetails {...props} handleTouchEvent={pressEventForStoreDetail}  />}
            </Tab.Screen > 
            <Tab.Screen name="Chat"   >
            { ()=> <StoreDetails {...props} handleTouchEvent={pressEventForStoreDetail}  />}
           </Tab.Screen > 
            
            <Tab.Screen name="Products"   >
            { ()=> <StoreDetails {...props} handleTouchEvent={pressEventForStoreDetail}  />}
            </Tab.Screen > 
            
      </Tab.Navigator>
    );
}
  

const storeDetailRecord = {
    image: '../../assets/icon.png',
    storeName: 'Bismillaha General Store',
    name: 'Malik Adnan',
    addressLine: 'Sabzi Mandi Islambad, shop no 201',
    businessTypes: ['General Store'],
    location: {
        type: 'Point',
        coordinates:[]
    },
    mobileNumber: '03015339780',
    
}

const StoreDetails = function ({route}) {
    const [storeDetails, setstoreDetails] = useState({});
    console.log('route.params', route.params)

    useEffect(() => {
        setstoreDetails(storeDetailRecord)
    }, []);
    
    return (
        <>
        <Text>Store detail page</Text>
        </>
    )
}
 

