import React, {useState} from 'react';
import {
  Button, StyleSheet, Text, View,
    ScrollView,
    Animated,
    FlatList, 
  Dimensions
} from 'react-native';


export default function BusinessTypes() {
    const [items, setitems] = useState([
      {key:1, name:'General Store & Kiryana'},
      {key:2, name:'Medical Store & Baby Care'},
      {key:3, name:'Fruit & Vegitables'},
      {key:4, name:'Cloths'},
     
    ]);
     return (
      
         <ScrollView horizontal={true} style={styles.horizentalList}>
             <Animated.FlatList
                 data={items}
                 keyExtractor={item => item.key}
                 renderItem={item => {
                     return <View><Text style={styles.item}>{ item.name }</Text></View>
                 }}
             >

             </Animated.FlatList>
         </ScrollView>
        
    //   <ScrollView horizontal={true} style={styles.horizentalList}>
    //     {
    //       items.map(it => {
    //         return <View key={it.key}>
    //           <Text style={styles.item} >{ it.name }</Text>
    //           </View>
    //       })
    //     }
        
        
    //   </ScrollView>
    );
}
  

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    horizentalList: {
      flex: 1,
      // flexDirection:'column',
        backgroundColor: '#fff',
      
      
    },
    item: {
      backgroundColor: '#4ae1fa',
       justifyContent: 'center',
      alignItems: 'center',
      textAlign:'center',
        margin: 5,
      
    }
  });
