import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { Button, StyleSheet, Text, View } from 'react-native';

import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
import { createMaterialTopTabNavigator } from '@react-navigation/material-top-tabs';



const LoginScreen = ({navigation}) => {
  return (
    <View
      style={{flex:1, alignItems:'center', justifyContent:'center'}}
    >
      <Text>Login Screen</Text>
      <Button
        title="Goto Detail screen"
      onPress={()=>{navigation.navigate('Details')}}
      ></Button>
    </View>
  )
}

const DetailScreen = () => {
  return (
    <View
      style={{flex:1, alignItems:'center', justifyContent:'center'}}
    >
      <Text>Detail Screen</Text>
    </View>
  )
}

const LoginStack = createNativeStackNavigator();
const HomeStack = createNativeStackNavigator();
const Drawer = createDrawerNavigator();
const Tab = createMaterialTopTabNavigator();

const LoginStackScreen = () => {
  return (
<LoginStack.Navigator >

        <LoginStack.Screen name="Login" component={LoginScreen}></LoginStack.Screen>

      </LoginStack.Navigator>
  )
}

const HomeStackScreen = () => {
  return (
<HomeStack.Navigator screenOptions={{
        headerStyle: {
          backgroundColor:'#009387'
        },
        headerTintColor: '#fff',
        headerTitleStyle: {
          fontWeight:'bold'
        }
      }}>

        <HomeStack.Screen name="Details" component={DetailScreen}></HomeStack.Screen>

      </HomeStack.Navigator>
  )
}


function MyTabs() {
  return (
    <Tab.Navigator>
      <Tab.Screen name="Login" component={LoginScreen} />
      <Tab.Screen name="Details" component={DetailScreen} />
    </Tab.Navigator>
  );
}
export default function App() {
  const [isLogin, setisLogin] = useState(false);

  return (
    <NavigationContainer>

      {isLogin === false &&
        <LoginStackScreen></LoginStackScreen>
      }

      {
        isLogin === true &&
        <Drawer.Navigator initialRouteName="Home">

        <Drawer.Screen name="Details" component={MyTabs} />
      </Drawer.Navigator>
      }


    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
