import React, {useState} from 'react';
import {
  Button, StyleSheet, Text, View,
  ScrollView
} from 'react-native';


import ScrollableTabView, { ScrollableTabBar } from 'react-native-scrollable-tab-view';

export default function BusinessTypes() {
    const [items, setitems] = useState([
      {key:1, name:'General Store & Kiryana'},
      {key:2, name:'Medical Store & Baby Care'},
      {key:3, name:'Fruit & Vegitables'},
      {key:4, name:'Cloths'},
     
    ]);
     return (
        <ScrollableTabView
        style={{ marginTop: 20 }}
        initialPage={0}
        renderTabBar={() => <ScrollableTabBar />}
      >
        <Text tabLabel='Tab #1'>My</Text>
        <Text tabLabel='Tab #2 word word'>favorite</Text>
        <Text tabLabel='Tab #3 word word word'>project</Text>
        <Text tabLabel='Tab #4 word word word word'>favorite</Text>
        <Text tabLabel='Tab #5'>project</Text>
      </ScrollableTabView>
    //   <ScrollView horizontal={true} style={styles.horizentalList}>
    //     {
    //       items.map(it => {
    //         return <View key={it.key}>
    //           <Text style={styles.item} >{ it.name }</Text>
    //           </View>
    //       })
    //     }
        
        
    //   </ScrollView>
    );
}
  

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    horizentalList: {
      flex: 1,
      // flexDirection:'column',
        backgroundColor: '#fff',
      
      
    },
    item: {
      backgroundColor: '#4ae1fa',
       justifyContent: 'center',
      alignItems: 'center',
      textAlign:'center',
        margin: 5,
      
    }
  });
