import React, {useState} from 'react';
import {
  Button, StyleSheet, Text, View,
    ScrollView,
    Image,
    TouchableOpacity,
    Animated,
    FlatList, 
  Dimensions,
  Alert
} from 'react-native';
import StoreDetails from "./StoreDetails";


const RegisteredStores = function (props) {
    
    const handlePressEvent = (name) => {
        console.log(name)
        
    }
    const storeDetail = {
        image: '../../assets/icon.png',
        storeName: 'Bismillaha General Store',
        name: 'Malik Adnan',
        addressLine: 'Sabzi Mandi Islambad, shop no 201',
        businessTypes: ['General Store'],
        location: {
            type: 'Point',
            coordinates:[]
        },
        mobileNumber: '03015339780',
        
    }

    return (
        <>
        <ScrollView
        style={styles.horizentalList}
        >
            {
                props.data.map((store, index) => { 
                    return  <TouchableOpacity
                        style={styles.item}
                        key={`${index}-store`}
                        onPress={()=>{handlePressEvent(store.id || index)}}
                    >
                       <Image
                            source={require('../../assets/icon.png')}
                            style={{
                                width: '95%', height: 200,
                                margin:'3%',
                            }}
                        />  
                        <Text>{ store.storeName }</Text>
                        <Text>{ store.name }</Text>
                        <Text>{ store.addressLine }</Text>
                    </TouchableOpacity>
                })
            }
           

            
            </ScrollView>
            <StoreDetails></StoreDetails>

            </>
        
    )
};

export default RegisteredStores;


const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    horizentalList: {
      flex: 1,
    //    flexDirection:'column',
        // backgroundColor: 'red',
      
      
    },
    item: {
      backgroundColor: '#4ae1fa',
    //    justifyContent: 'center',
    //   alignItems: 'center',
    //   textAlign:'center',
        margin: 5,
      
    }
  });
